-- Function structure for prevent_equipment_delete
-- ----------------------------
DROP FUNCTION IF EXISTS "joe"."prevent_equipment_delete"();
CREATE OR REPLACE FUNCTION "joe"."prevent_equipment_delete"()
  RETURNS "pg_catalog"."trigger" AS $BODY$
BEGIN
  IF EXISTS (SELECT 1 FROM maintenance_col WHERE device_no = OLD.device_no) THEN
    RAISE EXCEPTION '不能删除设备 %：存在维修记录，请先删除或归档相关维修记录', OLD.device_no;
  END IF;
  RETURN OLD;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

-- ----------------------------
-- Function structure for sp_delete_maintenance
-- ----------------------------
DROP FUNCTION IF EXISTS "joe"."sp_delete_maintenance"("p_maintenance_id" int8);
CREATE OR REPLACE FUNCTION "joe"."sp_delete_maintenance"("p_maintenance_id" int8)
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE
    v_old_no VARCHAR;
BEGIN
    -- 取出旧 maintenance_no（用于校验/日志等）
    SELECT maintenance_no INTO v_old_no FROM maintenance_col WHERE maintenance_id = p_maintenance_id;
    IF NOT FOUND THEN
        RAISE EXCEPTION '维护记录 % 不存在', p_maintenance_id;
    END IF;

    -- 删除记录
    DELETE FROM maintenance_col WHERE maintenance_id = p_maintenance_id;

    -- 不再维护 maintenance_no_lock 表（已移除该机制）
    RETURN;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE SECURITY DEFINER
  COST 100;

-- ----------------------------
-- Function structure for sp_insert_maintenance
-- ----------------------------
DROP FUNCTION IF EXISTS "joe"."sp_insert_maintenance"("p_maintenance_no" varchar, "p_device_no" varchar, "p_maintenance_time" timestamptz, "p_fault_type" varchar, "p_fault_desc" text, "p_repair_action" varchar, "p_repair_person" varchar, "p_cost" numeric, "p_duration_minutes" int4);
CREATE OR REPLACE FUNCTION "joe"."sp_insert_maintenance"("p_maintenance_no" varchar, "p_device_no" varchar, "p_maintenance_time" timestamptz, "p_fault_type" varchar, "p_fault_desc" text, "p_repair_action" varchar, "p_repair_person" varchar, "p_cost" numeric, "p_duration_minutes" int4)
  RETURNS "pg_catalog"."int8" AS $BODY$
DECLARE
    v_id    BIGINT;
    v_cnt   INT;
    v_mtime TIMESTAMP;
BEGIN
    -- cost 校验
    IF p_cost IS NOT NULL AND p_cost < 0 THEN
        RAISE EXCEPTION 'cost 必须 >= 0: %', p_cost;
    END IF;

    -- device 存在性校验
    SELECT COUNT(*) INTO v_cnt FROM equipment WHERE device_no = p_device_no;
    IF v_cnt = 0 THEN
        RAISE EXCEPTION '引用设备不存在: %', p_device_no;
    END IF;

    -- 生成自增 id（注意：序列名使用当前 schema）
    v_id := nextval(quote_ident(current_schema()) || '.seq_maintenance_id');

    -- 处理时间：若传入 timestamptz，则转换为 timestamp
    IF p_maintenance_time IS NULL THEN
        v_mtime := CURRENT_TIMESTAMP;
    ELSE
        v_mtime := p_maintenance_time::timestamp;
    END IF;

    -- 尝试插入，捕获唯一约束冲突
    BEGIN
            maintenance_id, maintenance_no, device_no, maintenance_time,
            fault_type, fault_desc, repair_action, repair_person,
            cost, duration_minutes, created_at, updated_at
        ) VALUES (
            v_id, p_maintenance_no, p_device_no, v_mtime,
            p_fault_type, p_fault_desc, p_repair_action, p_repair_person,
            p_cost, p_duration_minutes, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP
        );
    EXCEPTION WHEN unique_violation THEN
        -- maintenance_no 已存在（依赖数据库唯一约束）
        RAISE EXCEPTION 'maintenance_no 已存在: %', p_maintenance_no;
    END;

    RETURN v_id;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE SECURITY DEFINER
  COST 100;

-- ----------------------------
-- Function structure for sp_update_maintenance
-- ----------------------------
DROP FUNCTION IF EXISTS "joe"."sp_update_maintenance"("p_maintenance_id" int8, "p_maintenance_no" varchar, "p_device_no" varchar, "p_maintenance_time" timestamptz, "p_fault_type" varchar, "p_fault_desc" text, "p_repair_action" varchar, "p_repair_person" varchar, "p_cost" numeric, "p_duration_minutes" int4);
CREATE OR REPLACE FUNCTION "joe"."sp_update_maintenance"("p_maintenance_id" int8, "p_maintenance_no" varchar, "p_device_no" varchar, "p_maintenance_time" timestamptz, "p_fault_type" varchar, "p_fault_desc" text, "p_repair_action" varchar, "p_repair_person" varchar, "p_cost" numeric, "p_duration_minutes" int4)
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE
    v_old_no VARCHAR;
    v_cnt    INT;
    v_mtime  TIMESTAMP;
BEGIN
    -- 确认记录存在并取出旧 maintenance_no
    SELECT maintenance_no INTO v_old_no
    FROM maintenance_col
    WHERE maintenance_id = p_maintenance_id;
    IF NOT FOUND THEN
        RAISE EXCEPTION '维护记录 % 不存在', p_maintenance_id;
    END IF;

    -- cost 校验
    IF p_cost IS NOT NULL AND p_cost < 0 THEN
        RAISE EXCEPTION 'cost 必须 >= 0: %', p_cost;
    END IF;

    -- device 存在性校验
    SELECT COUNT(*) INTO v_cnt FROM equipment WHERE device_no = p_device_no;
    IF v_cnt = 0 THEN
        RAISE EXCEPTION '引用设备不存在: %', p_device_no;
    END IF;

    -- 处理时间：将 timestamptz 转为 timestamp（如果 p_maintenance_time 为 NULL 使用当前时间）
    IF p_maintenance_time IS NULL THEN
        v_mtime := CURRENT_TIMESTAMP;
    ELSE
        v_mtime := p_maintenance_time::timestamp;
    END IF;

    -- 执行更新并捕获唯一约束冲突（如 maintenance_no 与其它记录重复）
    BEGIN
        UPDATE maintenance_col
        SET
            maintenance_no   = p_maintenance_no,
            device_no        = p_device_no,
            maintenance_time = v_mtime,
            fault_type       = p_fault_type,
            fault_desc       = p_fault_desc,
            repair_action    = p_repair_action,
            repair_person    = p_repair_person,
            cost             = p_cost,
            duration_minutes = p_duration_minutes,
            updated_at       = CURRENT_TIMESTAMP
        WHERE maintenance_id = p_maintenance_id;
    EXCEPTION WHEN unique_violation THEN
        RAISE EXCEPTION 'maintenance_no 已存在: %', p_maintenance_no;
    END;

    RETURN;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE SECURITY DEFINER
  COST 100;

